//add1Minute(), remove1Minute(), add1Second(), remove1Second()
class Time24 {
	//toString,equals and hashCode as generated by eclipse
	/*omitted*/
	final int seconds;
	final int minutes;
	final int hours;
	Time24(int seconds, int minutes, int hours){
		this.seconds=limit60(seconds);
		this.minutes = limit60(minutes);
		this.hours=limitHours(hours);
	}
	private static int limit60(int v){// 0..59 ok
		if(v<0|| v>59){throw new IllegalArgumentException("limit60 over "+v);}
		return v;
	}
	private static int limitHours(int v){//0..23 ok
		if(v<0|| v>23){throw new IllegalArgumentException("limitHours over "+v);}
		return v;
	}
	static final Time24 twelve= new Time24(0,0,12);

	Time24 add1Hour() {
		int newHours = hours;
		if (newHours < 23) {
			newHours +=1;
		} else {
			newHours = 0;
		}
		return new Time24(seconds, minutes, newHours);
	}

	Time24 remove1Hour() {
		int newHours = hours;
		if (newHours > 0) {
			newHours -= 1;
		} else {
			newHours = 23;
		}
		return new Time24(seconds, minutes, newHours);
	}

	Time24 add1Minute() {
		int newHours = hours;
		int newMinutes = minutes;
		if (newMinutes < 59) {
			newMinutes += 1;
		} else {
			newMinutes = 0;
			if (newHours < 23) {
				newHours +=1;
			} else {
				newHours = 0;
			}
		}
		return new Time24(seconds, newMinutes, newHours);
	}

	Time24 remove1Minute() {
		int newHours = hours;
		int newMinutes = minutes;
		if (newMinutes > 0) {
			newMinutes -= 1;
		} else {
			newMinutes = 59;
			if (newHours > 0) {
				newHours -= 1;
			} else {
				newHours = 23;
			}
		}
		return new Time24(seconds, newMinutes, newHours);
	}

	Time24 add1Second() {
		int newHours = hours;
		int newMinutes = minutes;
		int newSeconds = seconds;
		if (newSeconds < 59) {
			newSeconds +=1;
		} else {
			newSeconds = 0;
			if (newMinutes < 59) {
				newMinutes += 1;
			} else {
				newMinutes = 0;
				if (newHours < 23) {
					newHours +=1;
				} else {
					newHours = 0;
				}
			}
		}
		return new Time24(newSeconds, newMinutes, newHours);
	}

	Time24 remove1Second() {
		int newSeconds = seconds;
		int newHours = hours;
		int newMinutes = minutes;
		if (newSeconds > 0) {
			newSeconds -= 1;
		} else {
			newSeconds = 59;
			if (newMinutes > 0) {
				newMinutes -= 1;
			} else {
				newMinutes = 59;
				if (newHours > 0) {
					newHours -= 1;
				} else {
					newHours = 23;
				}
			}
		}
		return new Time24(newSeconds, newMinutes, newHours);
	}

}

