    private void compareActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_compareActionPerformed
        
        Zone a = new Zone(new PositionArea(Integer.parseInt(x1.getText()),Integer.parseInt(y1.getText()),
                Integer.parseInt(w1.getText()),Integer.parseInt(h1.getText())), ZoneType.BLOCKAGE);
        Zone b = new Zone(new PositionArea(Integer.parseInt(x2.getText()),Integer.parseInt(y2.getText()),
                Integer.parseInt(w2.getText()),Integer.parseInt(h2.getText())), ZoneType.BLOCKAGE);

        if (a.overlaps(b))
        {
            output.setText("Overlap");
        }
        else
        {
            output.setText("No Overlap");
        }
    }//GEN-LAST:event_compareActionPerformed

